import useInputMine from "../hooks/use-input-mine";
import { useState, useEffect } from "react";

const BasicForm = (props) => {
  const [formIsValid, setFormIsValid] = useState(false);

  const {
    enteredValue: enteredFirstName,
    valueIsValid: firstNameIsValid,
    inputValueHasErros: firstNameHasErrors,
    valueInputChangeHandler: firstNameChangeHandler,
    valueInputBlurHandler: firstNameBlurHandler,
    resetInput: resetFirstName,
  } = useInputMine(validateFirstName);

  function validateFirstName(value) {
    return value.trim() !== "";
  }

  useEffect(() => {
    if (firstNameIsValid) {
      setFormIsValid(true);
    } else {
      setFormIsValid(false);
    }
  }, [firstNameIsValid]);

  function submitFormHandler(event) {
    event.preventDefault();
    if (!firstNameIsValid) {
      return;
    }
    resetFirstName();
  }

  return (
    <form onSubmit={submitFormHandler}>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            value={enteredFirstName}
            onChange={firstNameChangeHandler}
            onBlur={firstNameBlurHandler}
          />
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
